package Turma24;

public class RepeticaoEx1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int x;
				
		System.out.printf("\nNúmeros de 1000 a 1999 que quando dividos por 11 tem resto 5:");

				for (x=1000;x<=1999;x++) {
					
					if (x%11 == 5) {
					
					System.out.printf("\n"+x);

					}

				}

	
	}
}
